// +build cgo
// +build pow_avx
// +build amd64

package pow_test

import (
	. "github.com/loveandpeople-DAG/goClient/consts"
	"github.com/loveandpeople-DAG/goClient/curl"
	"github.com/loveandpeople-DAG/goClient/guards"
	"github.com/loveandpeople-DAG/goClient/pow"
	. "github.com/onsi/ginkgo"
	. "github.com/onsi/gomega"
)

var _ = Describe("AVXProofOfWork", func() {
	Context("AVXProofOfWork()", func() {
		rawTx := ""
		It("computes a correct nonce", func() {
			nonce, err := pow.AVXProofOfWork(rawTx, 10)
			rawTx = rawTx[:len(rawTx)-NonceTrinarySize/3] + nonce
			Expect(err).ToNot(HaveOccurred())
			hashedTrytes := curl.MustHashTrytes(rawTx)
			Expect(guards.IsTransactionHashWithMWM(hashedTrytes, 10)).To(BeTrue())
		})
	})

})
